import React, { useState, useEffect } from "react";

export default function FloatingInput({
  label,
  name,
  type = "text",
  value,
  onChange,
  onBlur,
  error = "",
  required = false,
  options = [],
  accept = "",
}) {
  const [isFocused, setIsFocused] = useState(false);

  const showLabelAbove = isFocused || value;

  useEffect(() => {
    if (value) setIsFocused(true);
  }, [value]);

  const inputBaseClass =
    "block w-full px-4 pt-5 pb-2 text-base bg-transparent border rounded-lg appearance-none focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-300 peer";

  const labelBaseClass =
    "absolute text-sm text-gray-500 dark:text-gray-400 duration-200 transform -translate-y-4 scale-75 top-2 z-10 origin-[0] left-3 peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-2.5 peer-focus:scale-75 peer-focus:-translate-y-4";

  return (
    <div className="relative w-full animate-fadeIn">
      {type === "select" ? (
        <>
          <select
            name={name}
            value={value}
            onChange={onChange}
            onBlur={(e) => {
              onBlur?.(e);
              setIsFocused(true);
            }}
            className={`${inputBaseClass} bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600`}
          >
            <option value="">-- Select {label} --</option>
            {options.map((opt) => (
              <option key={opt} value={opt}>
                {opt}
              </option>
            ))}
          </select>
        </>
      ) : type === "file" ? (
        <input
          type="file"
          name={name}
          onChange={onChange}
          accept={accept}
          className="block w-full text-sm text-gray-300 file:mr-4 file:py-2 file:px-4 file:rounded-lg file:border-0 file:text-sm file:font-semibold file:bg-blue-600 file:text-white hover:file:bg-blue-700 cursor-pointer"
        />
      ) : (
        <>
          <input
            type={type}
            name={name}
            value={value}
            onChange={onChange}
            onBlur={(e) => {
              onBlur?.(e);
              setIsFocused(true);
            }}
            placeholder=" "
            required={required}
            className={`${inputBaseClass} ${
              error
                ? "border-red-500 focus:ring-red-400 dark:border-red-400 dark:focus:ring-red-400"
                : "border-gray-300 dark:border-gray-600"
            } bg-white dark:bg-gray-800`}
          />
          <label htmlFor={name} className={labelBaseClass}>
            {label}
          </label>
        </>
      )}
      {error && (
        <p className="text-sm text-red-500 mt-1 px-1 transition-all duration-200 animate-shake">
          {error}
        </p>
      )}
    </div>
  );
}
