// components/FloatingInput.jsx
import React from "react";

export default function FloatingInput({
  label,
  name,
  type = "text",
  value,
  onChange,
  onBlur,
  error,
  accept,
  required = false,
  className = "",
}) {
  const isFile = type === "file";
  const baseStyle = `relative z-0 w-full group`;

  return (
    <div className={`${baseStyle} animate-fadeIn`}>
      {!isFile ? (
        <>
          <input
            type={type}
            name={name}
            id={name}
            value={value}
            onChange={onChange}
            onBlur={onBlur}
            required={required}
            className={`block py-2.5 px-2 w-full text-sm text-white bg-transparent border-0 border-b-2 border-gray-500 appearance-none focus:outline-none focus:ring-0 focus:border-blue-500 peer dark:text-white dark:border-gray-400 dark:focus:border-blue-400 ${className}`}
            placeholder=" "
          />
          <label
            htmlFor={name}
            className="absolute text-sm text-gray-300 dark:text-gray-400 duration-300 transform -translate-y-6 scale-75 top-2 left-2 origin-[0] peer-placeholder-shown:scale-100 peer-placeholder-shown:translate-y-2.5 peer-focus:scale-75 peer-focus:-translate-y-6"
          >
            {label}
          </label>
        </>
      ) : (
        <>
          <label className="block text-sm mb-1 text-gray-200">{label}</label>
          <input
            type="file"
            name={name}
            accept={accept}
            onChange={onChange}
            className="block w-full text-sm text-gray-300 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-600 file:text-white hover:file:bg-blue-700"
          />
        </>
      )}
      {error && <p className="text-red-400 text-xs mt-1">{error}</p>}
    </div>
  );
}
