import React from "react";

const FloatingSelect = ({ name, label, value, onChange, onBlur, options = [], error }) => {
  return (
    <div className="relative z-0 w-full group animate-fadeIn">
      <select
        name={name}
        id={name}
        value={value}
        onChange={onChange}
        onBlur={onBlur}
        className={`block px-2.5 pb-2.5 pt-4 w-full text-sm text-white bg-gray-800 dark:bg-gray-900 border rounded-lg appearance-none focus:outline-none focus:ring-0 focus:border-blue-500 peer ${
          error ? "border-red-500" : "border-gray-600"
        }`}
      >
        <option value="">-- Select --</option>
        {options.map((o) => (
          <option key={o} value={o}>
            {o}
          </option>
        ))}
      </select>
      <label
        htmlFor={name}
        className={`absolute text-sm text-gray-400 dark:text-gray-400 duration-300 transform -translate-y-4 scale-75 top-2 z-10 origin-[0] left-2.5 peer-focus:scale-75 peer-focus:-translate-y-4`}
      >
        {label}
      </label>
      {error && <p className="text-red-400 text-xs mt-1">{error}</p>}
    </div>
  );
};

export default FloatingSelect;
