import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./HomePage.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");
    let width = (canvas.width = window.innerWidth);
    let height = (canvas.height = window.innerHeight);
    let particles = [];

    for (let i = 0; i < 100; i++) {
      particles.push({
        x: Math.random() * width,
        y: Math.random() * height,
        r: Math.random() * 2 + 1,
        dx: Math.random() * 0.5 - 0.25,
        dy: Math.random() * 0.5 - 0.25,
      });
    }

    function draw() {
      ctx.clearRect(0, 0, width, height);
      particles.forEach((p) => {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
        ctx.fillStyle = "#ffffff88";
        ctx.fill();
        p.x += p.dx;
        p.y += p.dy;

        if (p.x < 0 || p.x > width) p.dx *= -1;
        if (p.y < 0 || p.y > height) p.dy *= -1;
      });
      requestAnimationFrame(draw);
    }

    draw();
  }, []);

  return <canvas id="bg-canvas" className="absolute top-0 left-0 z-0" />;
}

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateRegister = (form) => {
    const newErrors = {};
    if (!form.username.value) newErrors.username = "Username is required";
    if (!form.fullname.value) newErrors.fullname = "Full name is required";
    if (!form.institute.value) newErrors.institute = "Institute is required";
    if (!form.street.value) newErrors.street = "Street is required";
    if (!form.city.value) newErrors.city = "City is required";
    if (!form.pincode.value) newErrors.pincode = "Pincode is required";
    if (!form.age.value || form.age.value < 10 || form.age.value > 100)
      newErrors.age = "Age must be between 10 and 100";
    if (!form.course.value) newErrors.course = "Course is required";
    if (!form.email.value) newErrors.email = "Email is required";
    if (!form.phone.value) newErrors.phone = "Phone is required";
    if (!form.studentId.files[0])
      newErrors.studentId = "Student ID image is required";
    else if (!form.studentId.files[0].type.startsWith("image/"))
      newErrors.studentId = "File must be an image";
    if (!form.password.value) newErrors.password = "Password is required";
    if (form.password.value !== form.confirmPassword.value)
      newErrors.confirmPassword = "Passwords do not match";

    return newErrors;
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const form = e.target;
    const validationErrors = validateRegister(form);
    setErrors(validationErrors);
    if (Object.keys(validationErrors).length > 0) return;

    const formData = new FormData(form);
    try {
      await api.post("/api/auth/register", formData);
      toast.success("✅ Registered Successfully");
      setMode("login");
    } catch (err) {
      toast.error("❌ Register Failed: " + err.response?.data?.error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const form = e.target;
    const email = form.email.value;
    const password = form.password.value;
    const loginErrors = {};
    if (!email) loginErrors.email = "Email is required";
    if (!password) loginErrors.password = "Password is required";
    setErrors(loginErrors);
    if (Object.keys(loginErrors).length > 0) return;

    try {
      const res = await api.post("/api/auth/login", { email, password });
      toast.success("✅ Login Successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login Failed: " + err.response?.data?.error);
    }
  };

  return (
    <div className="relative h-screen overflow-auto flex items-center justify-center text-white">
      <BackgroundAnimation />
      <div className="form-container">
        <h1 className="title">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button onClick={() => setMode("login")} className={`tab ${mode === "login" ? "active" : ""}`}>Login</button>
          <button onClick={() => setMode("register")} className={`tab ${mode === "register" ? "active" : ""}`}>Register</button>
        </div>

        {mode === "login" ? (
          <form className="space-y-4 animate-fadeIn" onSubmit={handleLogin}>
            <input name="email" type="email" placeholder="Email" className="input" />
            {errors.email && <p className="error">{errors.email}</p>}
            <input name="password" type="password" placeholder="Password" className="input" />
            {errors.password && <p className="error">{errors.password}</p>}
            <button className="btn btn-login">Login</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                navigate("/dashboard");
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <form className="space-y-3 animate-fadeIn" onSubmit={handleRegister}>
            <input name="username" placeholder="Username" className="input" />
            {errors.username && <p className="error">{errors.username}</p>}

            <input name="fullname" placeholder="Full Name" className="input" />
            {errors.fullname && <p className="error">{errors.fullname}</p>}

            <input name="institute" placeholder="College/School" className="input" />
            {errors.institute && <p className="error">{errors.institute}</p>}

            <div className="flex gap-2">
              <input name="street" placeholder="Street" className="input" />
              <input name="city" placeholder="City" className="input" />
              <input name="pincode" placeholder="Pincode" className="input" />
            </div>
            {errors.street && <p className="error">{errors.street}</p>}
            {errors.city && <p className="error">{errors.city}</p>}
            {errors.pincode && <p className="error">{errors.pincode}</p>}

            <input name="age" type="number" placeholder="Age" className="input" />
            {errors.age && <p className="error">{errors.age}</p>}

            <select name="course" className="input">
              <option value="">Select Course</option>
              <option value="BSc">BSc</option>
              <option value="BCA">BCA</option>
              <option value="MCA">MCA</option>
              <option value="BTech">BTech</option>
            </select>
            {errors.course && <p className="error">{errors.course}</p>}

            <input name="email" type="email" placeholder="Email" className="input" />
            {errors.email && <p className="error">{errors.email}</p>}

            <input name="phone" type="tel" placeholder="+91 9876543210" className="input" />
            {errors.phone && <p className="error">{errors.phone}</p>}

            <label className="text-left block">Student ID Upload (Image only)</label>
            <input name="studentId" type="file" accept="image/*" className="input" />
            {errors.studentId && <p className="error">{errors.studentId}</p>}

            <input name="password" type="password" placeholder="Password" className="input" />
            {errors.password && <p className="error">{errors.password}</p>}

            <input name="confirmPassword" type="password" placeholder="Confirm Password" className="input" />
            {errors.confirmPassword && <p className="error">{errors.confirmPassword}</p>}

            <button className="btn btn-register">Register</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google signup success");
              }}
              onError={() => toast.error("❌ Google signup failed")}
            />
          </form>
        )}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
}
