import React, { useState } from "react";
import { GoogleLogin, GoogleOAuthProvider } from "@react-oauth/google";
import jwtDecode from "jwt-decode";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

const HomePage = () => {
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/auth/register", {
        email,
        name,
      });
      toast.success("✅ Registered successfully!");
      console.log(response.data);
    } catch (error) {
      toast.error("❌ Register Failed: " + (error?.response?.data?.message || "Unknown error"));
      console.error("Register Error:", error);
    }
  };

  const handleGoogleLogin = async (credentialResponse) => {
    const decoded = jwtDecode(credentialResponse.credential);
    console.log("Google decoded:", decoded);
    try {
      const response = await axios.post("http://localhost:5000/api/auth/google-login", {
        email: decoded.email,
        name: decoded.name,
      });
      toast.success("✅ Google Login Success!");
      console.log("Google Login Response:", response.data);
    } catch (error) {
      toast.error("❌ Google Login Failed: " + (error?.response?.data?.message || "Unknown error"));
      console.error("Google Login Error:", error);
    }
  };

  return (
    <GoogleOAuthProvider clientId="749131023603-4898vphf407t8msd8rqer0319p8d9264.apps.googleusercontent.com">
      <div style={{ padding: "2rem" }}>
        <h2>Register</h2>
        <form onSubmit={onSubmit}>
          <input
            type="text"
            placeholder="Enter your name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            style={{ marginBottom: "1rem", display: "block" }}
          />
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{ marginBottom: "1rem", display: "block" }}
          />
          <button type="submit">Register</button>
        </form>

        <h3>Or</h3>
        <GoogleLogin
          onSuccess={handleGoogleLogin}
          onError={() => toast.error("❌ Google Login Failed")}
        />

        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </GoogleOAuthProvider>
  );
};

export default HomePage;
