import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./HomePage.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    const stars = Array.from({ length: 200 }).map(() => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      radius: Math.random() * 1.5,
      opacity: Math.random(),
      speed: Math.random() * 0.5 + 0.2,
    }));

    const rockets = [
      {
        x: -50,
        y: canvas.height / 2,
        vx: 1.2,
        vy: -0.4,
        img: new Image(),
      },
    ];

    rockets[0].img.src =
      "https://cdn-icons-png.flaticon.com/512/1995/1995525.png";

    function draw() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      ctx.fillStyle = "#ffffff";
      stars.forEach((star) => {
        ctx.globalAlpha = star.opacity;
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.radius, 0, Math.PI * 2);
        ctx.fill();
        star.y += star.speed;
        if (star.y > canvas.height) {
          star.y = 0;
          star.x = Math.random() * canvas.width;
        }
      });

      ctx.globalAlpha = 1;

      rockets.forEach((rocket) => {
        ctx.drawImage(rocket.img, rocket.x, rocket.y, 60, 60);
        rocket.x += rocket.vx;
        rocket.y += rocket.vy;
        if (rocket.x > canvas.width || rocket.y < -60) {
          rocket.x = -60;
          rocket.y = Math.random() * canvas.height;
        }
      });

      requestAnimationFrame(draw);
    }

    draw();
  }, []);

  return <canvas id="bg-canvas" className="absolute top-0 left-0 w-full h-full z-0" />;
}

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const form = e.target;
    const data = {
      email: form.email.value,
      password: form.password.value,
    };
    try {
      const res = await api.post("/api/auth/login", data);
      toast.success("✅ Login Successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login Failed: " + err.response?.data?.error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    try {
      await api.post("/api/auth/register", formData);
      toast.success("✅ Registered Successfully");
      setMode("login");
    } catch (err) {
      toast.error("❌ Register Failed: " + err.response?.data?.error);
    }
  };

  return (
    <div className="relative h-screen flex items-center justify-center text-white">
      <BackgroundAnimation />
      <div className="form-container">
        <h1 className="text-3xl font-bold mb-4">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button
            onClick={() => setMode("login")}
            className={`tab ${mode === "login" ? "active" : ""}`}
          >
            Login
          </button>
          <button
            onClick={() => setMode("register")}
            className={`tab ${mode === "register" ? "active" : ""}`}
          >
            Register
          </button>
        </div>

        {mode === "login" ? (
          <form className="space-y-4 animate-fadeIn" onSubmit={handleLogin}>
            <input
              name="email"
              type="email"
              placeholder="Email"
              className="input"
              required
            />
            <input
              name="password"
              type="password"
              placeholder="Password"
              className="input"
              required
            />
            <button className="btn btn-login">Login</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                setMode("register"); // Switch to register tab to fill remaining details
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <form className="space-y-3 animate-fadeIn overflow-y-auto max-h-[70vh]" onSubmit={handleRegister}>
            <input name="username" type="text" placeholder="Username" required className="input" />
            <input name="fullname" type="text" placeholder="Full Name" className="input" />
            <input name="institute" type="text" placeholder="College/School" className="input" />
            <input name="address" type="text" placeholder="Address" className="input" />
            <input name="age" type="number" placeholder="Age" className="input" />
            <input name="course" type="text" placeholder="Course" className="input" />
            <input name="email" type="email" placeholder="Email" required className="input" />
            <input name="phone" type="tel" placeholder="Phone" className="input" />
            <input name="studentId" type="file" className="input" />
            <input name="password" type="password" placeholder="Password" required className="input" />
            <button className="btn btn-register">Register</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google signup success");
              }}
              onError={() => toast.error("❌ Google signup failed")}
            />
          </form>
        )}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
}
