import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "react-toastify/dist/ReactToastify.css";
import "./HomePage.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    // Generate stars
    let stars = Array.from({ length: 100 }, () => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      r: Math.random() * 1.5 + 0.5,
    }));

    // Rocket objects
    const rocketImgs = [
      "/assets/rocket1.png",
      "/assets/rocket2.png",
      "/assets/rocket3.png",
    ];
    let rockets = rocketImgs.map((src, i) => ({
      img: new Image(),
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      dx: 0.3 + Math.random() * 0.4,
      dy: 0.2 + Math.random() * 0.3,
      angle: Math.random() * 360,
    }));

    rockets.forEach((r, i) => (r.img.src = rocketImgs[i]));

    const animate = () => {
      ctx.fillStyle = "black";
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Draw stars
      ctx.fillStyle = "white";
      stars.forEach((s) => {
        ctx.beginPath();
        ctx.arc(s.x, s.y, s.r, 0, Math.PI * 2);
        ctx.fill();
      });

      // Draw rockets
      rockets.forEach((r) => {
        ctx.save();
        ctx.translate(r.x, r.y);
        ctx.rotate((r.angle * Math.PI) / 180);
        ctx.drawImage(r.img, -25, -25, 50, 50);
        ctx.restore();

        r.x += r.dx;
        r.y += r.dy;
        r.angle += 0.5;

        if (r.x > canvas.width) r.x = -50;
        if (r.y > canvas.height) r.y = -50;
      });

      requestAnimationFrame(animate);
    };

    animate();
  }, []);

  return (
    <canvas
      id="bg-canvas"
      className="fixed top-0 left-0 w-full h-full -z-10"
    />
  );
}

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const form = e.target;
    const data = {
      email: form.email.value,
      password: form.password.value,
    };
    try {
      const res = await api.post("/api/auth/login", data);
      toast.success("✅ Login Successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login Failed: " + err.response?.data?.error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    try {
      await api.post("/api/auth/register", formData);
      toast.success("✅ Registered Successfully");
      setMode("login");
    } catch (err) {
      toast.error("❌ Register Failed: " + err.response?.data?.error);
    }
  };

  return (
    <div className="relative min-h-screen flex items-center justify-center text-white overflow-y-auto">
      <BackgroundAnimation />
      <div className="form-container">
        <h1 className="text-3xl font-bold mb-4">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button
            onClick={() => setMode("login")}
            className={`tab ${mode === "login" ? "active" : ""}`}
          >
            Login
          </button>
          <button
            onClick={() => setMode("register")}
            className={`tab ${mode === "register" ? "active" : ""}`}
          >
            Register
          </button>
        </div>

        {mode === "login" ? (
          <form className="space-y-4 animate-fadeIn" onSubmit={handleLogin}>
            <input name="email" type="email" placeholder="Email" className="input" required />
            <input name="password" type="password" placeholder="Password" className="input" required />
            <button className="btn btn-login">Login</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                setMode("register"); // switch to register if new
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <form className="space-y-3 animate-fadeIn" onSubmit={handleRegister}>
            <input name="username" type="text" placeholder="Username" className="input" required />
            <input name="fullname" type="text" placeholder="Full Name" className="input" />
            <input name="institute" type="text" placeholder="College/School" className="input" />
            <input name="address" type="text" placeholder="Address" className="input" />
            <input name="age" type="number" placeholder="Age" className="input" />
            <input name="course" type="text" placeholder="Course" className="input" />
            <input name="email" type="email" placeholder="Email" className="input" required />
            <input name="phone" type="tel" placeholder="Phone" className="input" />
            <input name="studentId" type="file" className="input" />
            <input name="password" type="password" placeholder="Password" className="input" required />
            <button className="btn btn-register">Register</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google signup success");
              }}
              onError={() => toast.error("❌ Google signup failed")}
            />
          </form>
        )}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
}
