// client/src/pages/Dashboard.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    try {
      const userData = JSON.parse(localStorage.getItem("user"));
      if (!userData) throw new Error("No user found");
      setUser(userData);
    } catch {
      navigate("/");
    }
  }, [navigate]);

  const logout = () => {
    localStorage.clear();
    navigate("/");
  };

  if (!user) return null;

  return (
    <div className="p-10 text-center">
      <h1 className="text-3xl font-bold">ðŸ‘‹ Welcome, {user.fullname || user.username}!</h1>
      <p className="text-lg mt-2">Email: {user.email}</p>
      <p className="text-lg">Institute: {user.institute}</p>
      <button onClick={logout} className="mt-6 px-6 py-2 bg-red-500 text-white rounded hover:bg-red-600">
        Logout
      </button>
    </div>
  );
}
