import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    } else {
      navigate("/"); // redirect if not logged in
    }
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/");
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-purple-700 to-indigo-900 text-white">
      <div className="bg-black bg-opacity-70 p-10 rounded-xl shadow-xl w-full max-w-xl">
        <h1 className="text-3xl font-bold mb-4">ðŸŽ‰ Welcome, {user?.username}</h1>
        <p><strong>Full Name:</strong> {user?.fullname}</p>
        <p><strong>Email:</strong> {user?.email}</p>
        <p><strong>Phone:</strong> {user?.phone}</p>
        <p><strong>Institute:</strong> {user?.institute}</p>
        <p><strong>Course:</strong> {user?.course}</p>
        <button
          onClick={handleLogout}
          className="mt-6 px-6 py-2 bg-red-600 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>
    </div>
  );
}
