import React, { useState, useEffect } from "react";
import FloatingInput from "./components/FloatingInput";
import FloatingSelect from "./components/FloatingSelect";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "react-toastify/dist/ReactToastify.css";

const initialForm = {
  username: "",
  fullname: "",
  institute: "",
  street: "",
  city: "",
  pincode: "",
  age: "",
  course: "",
  email: "",
  phone: "",
  countryCode: "+91",
  studentId: null,
  password: "",
  confirmPassword: "",
};

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const [formData, setFormData] = useState(initialForm);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validate = (field, value) => {
    if (["username", "fullname", "institute", "street", "city"].includes(field)) {
      return /^[A-Za-z ]+$/.test(value) ? "" : "Only letters allowed";
    }
    if (["pincode", "phone", "age"].includes(field)) {
      return /^[0-9]+$/.test(value) ? "" : "Only numbers allowed";
    }
    switch (field) {
      case "email":
        return /\S+@\S+\.\S+/.test(value) ? "" : "Invalid email format";
      case "password":
        return value.length >= 6 ? "" : "Minimum 6 characters required";
      case "confirmPassword":
        return value === formData.password ? "" : "Passwords do not match";
      case "course":
        return value ? "" : "Please select a course";
      default:
        return "";
    }
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    const val = name === "studentId" ? files[0] : value;
    setFormData((prev) => ({ ...prev, [name]: val }));
    if (name !== "studentId") {
      setErrors((prev) => ({ ...prev, [name]: validate(name, val) }));
    }
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;
    setErrors((prev) => ({ ...prev, [name]: validate(name, value) }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const emailErr = validate("email", formData.email);
    const passErr = validate("password", formData.password);
    if (emailErr || passErr) {
      setErrors({ email: emailErr, password: passErr });
      toast.error("❌ Fix login errors");
      return;
    }
    try {
      const res = await api.post("/api/auth/login", {
        email: formData.email,
        password: formData.password,
      });
      toast.success("✅ Login successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch {
      toast.error("❌ Login failed");
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const allErrors = {};
    for (const key in formData) {
      if (key === "studentId") continue;
      const err = validate(key, formData[key]);
      if (err) allErrors[key] = err;
    }
    if (Object.keys(allErrors).length) {
      setErrors(allErrors);
      toast.error("❌ Fix registration errors");
      return;
    }
    const data = new FormData();
    Object.entries(formData).forEach(([k, v]) => data.append(k, v));
    try {
      await api.post("/api/auth/register", data);
      toast.success("✅ Registration successful");
      setFormData(initialForm);
      setMode("login");
    } catch (err) {
      toast.error("❌ Register failed: " + err.response?.data?.error);
    }
  };

  return (
    <div className="relative h-screen flex items-center justify-center text-white overflow-auto dark:bg-gray-950 bg-gradient-to-r from-blue-900 to-blue-700">
      <div className="bg-black/70 rounded-2xl shadow-lg w-full max-w-xl p-8 backdrop-blur-md overflow-y-auto max-h-[90vh] animate-fadeIn">
        <h1 className="text-3xl font-bold mb-6 text-center">Student Event Portal</h1>
        <div className="flex justify-center gap-6 mb-6">
          <button onClick={() => setMode("login")} className={`px-4 py-2 rounded-md transition ${mode === "login" ? "bg-blue-600" : "bg-gray-700"}`}>Login</button>
          <button onClick={() => setMode("register")} className={`px-4 py-2 rounded-md transition ${mode === "register" ? "bg-green-600" : "bg-gray-700"}`}>Register</button>
        </div>

        {mode === "login" ? (
          <form onSubmit={handleLogin} className="space-y-4">
            <FloatingInput label="Email" name="email" type="email" value={formData.email} onChange={handleChange} onBlur={handleBlur} error={errors.email} />
            <FloatingInput label="Password" name="password" type="password" value={formData.password} onChange={handleChange} onBlur={handleBlur} error={errors.password} />
            <button className="w-full bg-blue-600 hover:bg-blue-700 transition py-2 rounded-lg text-white font-semibold">Login</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                navigate("/dashboard");
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <form onSubmit={handleRegister} className="space-y-4" encType="multipart/form-data">
            <FloatingInput label="Username" name="username" value={formData.username} onChange={handleChange} onBlur={handleBlur} error={errors.username} />
            <FloatingInput label="Full Name" name="fullname" value={formData.fullname} onChange={handleChange} onBlur={handleBlur} error={errors.fullname} />
            <FloatingInput label="Institute" name="institute" value={formData.institute} onChange={handleChange} onBlur={handleBlur} error={errors.institute} />

            <div className="flex gap-2">
              <FloatingInput label="Street" name="street" value={formData.street} onChange={handleChange} onBlur={handleBlur} error={errors.street} />
              <FloatingInput label="City" name="city" value={formData.city} onChange={handleChange} onBlur={handleBlur} error={errors.city} />
              <FloatingInput label="Pincode" name="pincode" value={formData.pincode} onChange={handleChange} onBlur={handleBlur} error={errors.pincode} />
            </div>

            <FloatingInput label="Age" name="age" type="number" value={formData.age} onChange={handleChange} onBlur={handleBlur} error={errors.age} />
            <FloatingSelect label="Course" name="course" value={formData.course} onChange={handleChange} onBlur={handleBlur} error={errors.course} options={["BCA", "MCA", "B.Tech", "MBA"]} />
            <FloatingInput label="Email" name="email" type="email" value={formData.email} onChange={handleChange} onBlur={handleBlur} error={errors.email} />

            <div className="flex gap-2">
              <select name="countryCode" value={formData.countryCode} onChange={handleChange} className="w-24 p-2 bg-gray-900 border border-gray-700 rounded-md text-white">
                <option value="+91">+91</option>
                <option value="+1">+1</option>
                <option value="+44">+44</option>
              </select>
              <FloatingInput label="Phone" name="phone" value={formData.phone} onChange={handleChange} onBlur={handleBlur} error={errors.phone} />
            </div>

            <label className="text-sm">Upload Student ID</label>
            <input name="studentId" type="file" accept="image/*" className="block w-full p-2 border border-gray-700 rounded-md text-white bg-gray-900" onChange={handleChange} />

            <FloatingInput label="Password" name="password" type="password" value={formData.password} onChange={handleChange} onBlur={handleBlur} error={errors.password} />
            <FloatingInput label="Confirm Password" name="confirmPassword" type="password" value={formData.confirmPassword} onChange={handleChange} onBlur={handleBlur} error={errors.confirmPassword} />

            <button className="w-full bg-green-600 hover:bg-green-700 transition py-2 rounded-lg text-white font-semibold">Register</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google signup success");
              }}
              onError={() => toast.error("❌ Google signup failed")}
            />
          </form>
        )}
        <ToastContainer />
      </div>
    </div>
  );
}
