import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "react-toastify/dist/ReactToastify.css";
import "./HomePage.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");
    let width = (canvas.width = window.innerWidth);
    let height = (canvas.height = window.innerHeight);
    let particles = [];

    for (let i = 0; i < 100; i++) {
      particles.push({
        x: Math.random() * width,
        y: Math.random() * height,
        r: Math.random() * 2 + 1,
        dx: Math.random() * 0.5 - 0.25,
        dy: Math.random() * 0.5 - 0.25,
      });
    }

    function draw() {
      ctx.clearRect(0, 0, width, height);
      particles.forEach((p) => {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
        ctx.fillStyle = "#ffffff88";
        ctx.fill();
        p.x += p.dx;
        p.y += p.dy;

        if (p.x < 0 || p.x > width) p.dx *= -1;
        if (p.y < 0 || p.y > height) p.dy *= -1;
      });
      requestAnimationFrame(draw);
    }

    draw();
  }, []);

  return <canvas id="bg-canvas" className="absolute top-0 left-0 z-0" />;
}

const initialForm = {
  username: "",
  fullname: "",
  institute: "",
  street: "",
  city: "",
  pincode: "",
  age: "",
  course: "",
  email: "",
  phone: "",
  studentId: null,
  password: "",
  confirmPassword: "",
  countryCode: "+91"
};

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const [formData, setFormData] = useState(initialForm);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validate = (field, value) => {
    switch (field) {
      case "email":
        return /\S+@\S+\.\S+/.test(value) ? "" : "Invalid email";
      case "password":
        return value.length >= 6 ? "" : "Password must be at least 6 characters";
      case "confirmPassword":
        return value === formData.password ? "" : "Passwords do not match";
      case "username":
      case "fullname":
      case "institute":
      case "street":
      case "city":
      case "pincode":
      case "phone":
        return value ? "" : "This field is required";
      case "age":
        return value >= 10 && value <= 100 ? "" : "Enter a valid age";
      case "course":
        return value ? "" : "Select a course";
      default:
        return "";
    }
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;
    const errorMsg = validate(name, value);
    setErrors((prev) => ({ ...prev, [name]: errorMsg }));
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    const val = name === "studentId" ? files[0] : value;
    setFormData((prev) => ({ ...prev, [name]: val }));
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    let allErrors = {};
    Object.keys(formData).forEach((key) => {
      const val = formData[key];
      if (key === "studentId") return;
      const error = validate(key, val);
      if (error) allErrors[key] = error;
    });

    if (Object.keys(allErrors).length) {
      setErrors(allErrors);
      toast.error("❌ Please fix the errors");
      return;
    }

    const data = new FormData();
    Object.entries(formData).forEach(([key, val]) => data.append(key, val));

    try {
      await api.post("/api/auth/register", data);
      toast.success("✅ Registered successfully");
      setMode("login");
      setFormData(initialForm);
    } catch (err) {
      toast.error("❌ " + err.response?.data?.error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const emailErr = validate("email", formData.email);
    const passErr = validate("password", formData.password);
    if (emailErr || passErr) {
      setErrors({ email: emailErr, password: passErr });
      toast.error("❌ Fix login errors");
      return;
    }

    try {
      const res = await api.post("/api/auth/login", {
        email: formData.email,
        password: formData.password,
      });
      localStorage.setItem("token", res.data.token);
      toast.success("✅ Login successful");
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login failed");
    }
  };

  return (
    <div className="relative h-screen flex items-center justify-center text-white overflow-auto">
      <BackgroundAnimation />
      <div className="form-container">
        <h1 className="text-3xl font-bold mb-4">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button onClick={() => setMode("login")} className={`tab ${mode === "login" ? "active" : ""}`}>Login</button>
          <button onClick={() => setMode("register")} className={`tab ${mode === "register" ? "active" : ""}`}>Register</button>
        </div>

        {mode === "login" ? (
          <form className="space-y-4 animate-fadeIn" onSubmit={handleLogin}>
            <input name="email" placeholder="Email" onChange={handleChange} onBlur={handleBlur} className="input" />
            {errors.email && <p className="error">{errors.email}</p>}
            <input name="password" type="password" placeholder="Password" onChange={handleChange} onBlur={handleBlur} className="input" />
            {errors.password && <p className="error">{errors.password}</p>}
            <button className="btn btn-login">Login</button>
          </form>
        ) : (
          <form className="space-y-3 animate-fadeIn" onSubmit={handleRegister} encType="multipart/form-data">
            <input name="username" placeholder="Username" onChange={handleChange} onBlur={handleBlur} className="input" />
            {errors.username && <p className="error">{errors.username}</p>}
            <input name="fullname" placeholder="Full Name" onChange={handleChange} onBlur={handleBlur} className="input" />
            <input name="institute" placeholder="Institute" onChange={handleChange} onBlur={handleBlur} className="input" />
            <div className="flex gap-2">
              <input name="street" placeholder="Street" onChange={handleChange} onBlur={handleBlur} className="input" />
              <input name="city" placeholder="City" onChange={handleChange} onBlur={handleBlur} className="input" />
              <input name="pincode" placeholder="Pincode" onChange={handleChange} onBlur={handleBlur} className="input" />
            </div>
            <input name="age" placeholder="Age" type="number" onChange={handleChange} onBlur={handleBlur} className="input" />
            {errors.age && <p className="error">{errors.age}</p>}
            <select name="course" onChange={handleChange} onBlur={handleBlur} className="input">
              <option value="">-- Select Course --</option>
              <option value="BCA">BCA</option>
              <option value="MCA">MCA</option>
              <option value="B.Tech">B.Tech</option>
              <option value="MBA">MBA</option>
            </select>
            {errors.course && <p className="error">{errors.course}</p>}
            <input name="email" placeholder="Email" onChange={handleChange} onBlur={handleBlur} className="input" />
            {errors.email && <p className="error">{errors.email}</p>}
            <div className="flex gap-2">
              <select name="countryCode" onChange={handleChange} className="input max-w-[100px]">
                <option value="+91">+91</option>
                <option value="+1">+1</option>
                <option value="+44">+44</option>
              </select>
              <input name="phone" placeholder="Phone Number" onChange={handleChange} onBlur={handleBlur} className="input" />
            </div>
            <input type="file" name="studentId" accept="image/*" onChange={handleChange} className="input" />
            <input name="password" placeholder="Password" type="password" onChange={handleChange} onBlur={handleBlur} className="input" />
            {errors.password && <p className="error">{errors.password}</p>}
            <input name="confirmPassword" placeholder="Confirm Password" type="password" onChange={handleChange} onBlur={handleBlur} className="input" />
            {errors.confirmPassword && <p className="error">{errors.confirmPassword}</p>}
            <button className="btn btn-register">Register</button>
          </form>
        )}
        <ToastContainer />
      </div>
    </div>
  );
}
