import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./HomePage.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");
    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;
    const stars = Array.from({ length: 100 }, () => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      r: Math.random() * 2,
    }));

    const draw = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      stars.forEach((star) => {
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.r, 0, Math.PI * 2);
        ctx.fillStyle = "#fff";
        ctx.fill();
      });
      requestAnimationFrame(draw);
    };
    draw();
  }, []);

  return <canvas id="bg-canvas" className="absolute top-0 left-0 z-0" />;
}

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validateRegister = (form) => {
    const err = {};
    const age = parseInt(form.age.value, 10);

    if (!form.username.value) err.username = "Username is required";
    if (!form.email.value || !/\S+@\S+\.\S+/.test(form.email.value)) err.email = "Valid email required";
    if (!form.password.value) err.password = "Password is required";
    if (form.password.value !== form.confirmPassword.value) err.confirmPassword = "Passwords do not match";
    if (!form.fullname.value) err.fullname = "Name is required";
    if (isNaN(age) || age < 10 || age > 100) err.age = "Enter a valid age (10-100)";
    if (!form.course.value) err.course = "Select a course";
    if (!form.phone.value || !/^[0-9]{10}$/.test(form.phone.value)) err.phone = "Valid 10-digit phone required";
    if (!form.studentId.files[0]?.type.startsWith("image/")) err.studentId = "Only image files allowed";

    setErrors(err);
    return Object.keys(err).length === 0;
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (!validateRegister(e.target)) return;

    const formData = new FormData(e.target);
    try {
      await api.post("/api/auth/register", formData);
      toast.success("✅ Registered Successfully");
      setMode("login");
    } catch (err) {
      toast.error("❌ Register Failed: " + err.response?.data?.error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const form = e.target;
    const err = {};
    if (!form.email.value) err.email = "Email is required";
    if (!form.password.value) err.password = "Password is required";
    setErrors(err);
    if (Object.keys(err).length > 0) return;

    try {
      const res = await api.post("/api/auth/login", {
        email: form.email.value,
        password: form.password.value,
      });
      toast.success("✅ Login Successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login Failed: " + err.response?.data?.error);
    }
  };

  return (
    <div className="relative min-h-screen flex items-center justify-center text-white overflow-y-auto">
      <BackgroundAnimation />
      <div className="form-container">
        <h1 className="text-3xl font-bold mb-4">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button
            onClick={() => setMode("login")}
            className={`tab ${mode === "login" ? "active" : ""}`}
          >
            Login
          </button>
          <button
            onClick={() => setMode("register")}
            className={`tab ${mode === "register" ? "active" : ""}`}
          >
            Register
          </button>
        </div>

        {mode === "login" ? (
          <form className="space-y-4 animate-fadeIn" onSubmit={handleLogin}>
            <div>
              <input name="email" type="email" placeholder="Email" className="input" />
              {errors.email && <p className="error">{errors.email}</p>}
            </div>
            <div>
              <input name="password" type="password" placeholder="Password" className="input" />
              {errors.password && <p className="error">{errors.password}</p>}
            </div>
            <button className="btn btn-login">Login</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                navigate("/dashboard");
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <form className="space-y-3 animate-fadeIn" onSubmit={handleRegister}>
            <div>
              <input name="username" type="text" placeholder="Username" className="input" />
              {errors.username && <p className="error">{errors.username}</p>}
            </div>
            <div>
              <input name="fullname" type="text" placeholder="Full Name" className="input" />
              {errors.fullname && <p className="error">{errors.fullname}</p>}
            </div>
            <div className="flex gap-2">
              <input name="address1" type="text" placeholder="Street" className="input" />
              <input name="address2" type="text" placeholder="City" className="input" />
              <input name="address3" type="text" placeholder="Pincode" className="input" />
            </div>
            <div>
              <input name="age" type="number" placeholder="Age" className="input" />
              {errors.age && <p className="error">{errors.age}</p>}
            </div>
            <div>
              <select name="course" className="input">
                <option value="">Select Course</option>
                <option value="BTech">BTech</option>
                <option value="MTech">MTech</option>
                <option value="MCA">MCA</option>
              </select>
              {errors.course && <p className="error">{errors.course}</p>}
            </div>
            <div>
              <input name="email" type="email" placeholder="Email" className="input" />
              {errors.email && <p className="error">{errors.email}</p>}
            </div>
            <div className="flex gap-2">
              <select name="countryCode" className="input w-1/3">
                <option value="+91">+91</option>
                <option value="+1">+1</option>
              </select>
              <input name="phone" type="tel" placeholder="Phone" className="input w-2/3" />
              {errors.phone && <p className="error">{errors.phone}</p>}
            </div>
            <div>
              <label className="block text-left mb-1">Student ID Upload</label>
              <input name="studentId" type="file" accept="image/*" className="input" />
              {errors.studentId && <p className="error">{errors.studentId}</p>}
            </div>
            <div>
              <input name="password" type="password" placeholder="Password" className="input" />
              {errors.password && <p className="error">{errors.password}</p>}
            </div>
            <div>
              <input name="confirmPassword" type="password" placeholder="Confirm Password" className="input" />
              {errors.confirmPassword && <p className="error">{errors.confirmPassword}</p>}
            </div>
            <button className="btn btn-register">Register</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google signup success");
              }}
              onError={() => toast.error("❌ Google signup failed")}
            />
          </form>
        )}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
}
