import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./HomePage.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");
    let width = (canvas.width = window.innerWidth);
    let height = (canvas.height = window.innerHeight);
    let particles = [];

    for (let i = 0; i < 100; i++) {
      particles.push({
        x: Math.random() * width,
        y: Math.random() * height,
        r: Math.random() * 2 + 1,
        dx: Math.random() * 0.5 - 0.25,
        dy: Math.random() * 0.5 - 0.25,
      });
    }

    function draw() {
      ctx.clearRect(0, 0, width, height);
      particles.forEach((p) => {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
        ctx.fillStyle = "#ffffff88";
        ctx.fill();
        p.x += p.dx;
        p.y += p.dy;

        if (p.x < 0 || p.x > width) p.dx *= -1;
        if (p.y < 0 || p.y > height) p.dy *= -1;
      });
      requestAnimationFrame(draw);
    }

    draw();
  }, []);

  return <canvas id="bg-canvas" className="absolute top-0 left-0 z-0" />;
}

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const form = e.target;
    const data = {
      email: form.email.value,
      password: form.password.value,
    };
    try {
      const res = await api.post("/api/auth/login", data);
      toast.success("✅ Login Successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login Failed: " + err.response?.data?.error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const age = parseInt(formData.get("age"), 10);
    const password = formData.get("password");
    const confirmPassword = formData.get("confirmPassword");

    if (password !== confirmPassword) {
      toast.error("❌ Passwords do not match");
      return;
    }

    if (isNaN(age) || age < 5 || age > 100) {
      toast.error("❌ Enter a valid age between 5 and 100");
      return;
    }

    try {
      await api.post("/api/auth/register", formData);
      toast.success("✅ Registered Successfully");
      setMode("login");
    } catch (err) {
      toast.error("❌ Register Failed: " + err.response?.data?.error);
    }
  };

  return (
    <div className="relative h-screen flex items-center justify-center text-white">
      <BackgroundAnimation />
      <div className="form-container overflow-y-auto max-h-screen p-6">
        <h1 className="text-3xl font-bold mb-4">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button
            onClick={() => setMode("login")}
            className={`tab ${mode === "login" ? "active" : ""}`}
          >
            Login
          </button>
          <button
            onClick={() => setMode("register")}
            className={`tab ${mode === "register" ? "active" : ""}`}
          >
            Register
          </button>
        </div>

        {mode === "login" ? (
          <form className="space-y-4 animate-fadeIn" onSubmit={handleLogin}>
            <input
              name="email"
              type="email"
              placeholder="Email"
              className="input"
              required
            />
            <input
              name="password"
              type="password"
              placeholder="Password"
              className="input"
              required
            />
            <button className="btn btn-login">Login</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                console.log("Google User:", user);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                navigate("/dashboard");
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <div className="scrollable-form">
            <form className="space-y-3 animate-fadeIn" onSubmit={handleRegister}>
              <input name="username" type="text" placeholder="User Name" required className="input" />
              <input name="fullname" type="text" placeholder="Full Name" className="input" />
              <input name="institute" type="text" placeholder="College/School" className="input" />
              <div className="grid grid-cols-3 gap-2">
                <input name="address_line1" type="text" placeholder="Street" className="input" />
                <input name="address_line2" type="text" placeholder="City" className="input" />
                <input name="address_line3" type="text" placeholder="Pincode" className="input" />
              </div>
              <input name="age" type="number" placeholder="Age" className="input" required />
              <select name="course" className="input">
                <option value="">Select Course</option>
                <option value="BSc">BSc</option>
                <option value="BCA">BCA</option>
                <option value="MCA">MCA</option>
                <option value="BTech">BTech</option>
                <option value="Other">Other</option>
              </select>
              <input name="email" type="email" placeholder="Email" required className="input" />
              <div className="flex gap-2">
                <select name="countryCode" className="input w-1/3">
                  <option value="+91">+91</option>
                  <option value="+1">+1</option>
                  <option value="+44">+44</option>
                </select>
                <input name="phone" type="tel" placeholder="Phone" className="input w-2/3" />
              </div>
              <label className="block text-left text-sm mb-1">Student ID Upload</label>
              <input name="studentId" type="file" accept="image/*" className="input" />
              <input name="password" type="password" placeholder="Password" required className="input" />
              <input name="confirmPassword" type="password" placeholder="Confirm Password" required className="input" />
              <button className="btn btn-register">Register</button>
              <GoogleLogin
                onSuccess={(cred) => {
                  const user = jwtDecode(cred.credential);
                  console.log("Google Signup User:", user);
                  toast.success("✅ Google signup success");
                }}
                onError={() => toast.error("❌ Google signup failed")}
              />
            </form>
          </div>
        )}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
}
