import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import FloatingInput from "../components/FloatingInput"; // Reusable component
import "react-toastify/dist/ReactToastify.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");
    let width = (canvas.width = window.innerWidth);
    let height = (canvas.height = window.innerHeight);
    let particles = [];

    for (let i = 0; i < 100; i++) {
      particles.push({
        x: Math.random() * width,
        y: Math.random() * height,
        r: Math.random() * 2 + 1,
        dx: Math.random() * 0.5 - 0.25,
        dy: Math.random() * 0.5 - 0.25,
      });
    }

    function draw() {
      ctx.clearRect(0, 0, width, height);
      particles.forEach((p) => {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
        ctx.fillStyle = "#ffffff88";
        ctx.fill();
        p.x += p.dx;
        p.y += p.dy;
        if (p.x < 0 || p.x > width) p.dx *= -1;
        if (p.y < 0 || p.y > height) p.dy *= -1;
      });
      requestAnimationFrame(draw);
    }

    draw();
  }, []);

  return <canvas id="bg-canvas" className="absolute top-0 left-0 w-full h-full z-0" />;
}

const initialForm = {
  username: "", fullname: "", institute: "", street: "", city: "", pincode: "",
  age: "", course: "", email: "", phone: "", countryCode: "+91", studentId: null,
  password: "", confirmPassword: ""
};

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const [formData, setFormData] = useState(initialForm);
  const [errors, setErrors] = useState({});
  const navigate = useNavigate();

  const validate = (field, value) => {
    switch (field) {
      case "email": return /\S+@\S+\.\S+/.test(value) ? "" : "Invalid email";
      case "password": return value.length >= 6 ? "" : "At least 6 characters";
      case "username":
      case "fullname":
      case "institute":
      case "course": return value.trim() ? "" : "Required field";
      case "age": return value >= 10 && value <= 100 ? "" : "Valid age (10-100)";
      case "phone": return /^\d{10}$/.test(value) ? "" : "10 digit number";
      case "confirmPassword":
        return value === formData.password ? "" : "Passwords do not match";
      default: return "";
    }
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    const val = name === "studentId" ? files[0] : value;
    setFormData((prev) => ({ ...prev, [name]: val }));
    if (name !== "studentId") {
      const errorMsg = validate(name, val);
      setErrors((prev) => ({ ...prev, [name]: errorMsg }));
    }
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;
    const errorMsg = validate(name, value);
    setErrors((prev) => ({ ...prev, [name]: errorMsg }));
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const emailErr = validate("email", formData.email);
    const passErr = validate("password", formData.password);
    if (emailErr || passErr) {
      setErrors({ email: emailErr, password: passErr });
      toast.error("❌ Fix login errors");
      return;
    }
    try {
      const res = await api.post("/api/auth/login", {
        email: formData.email, password: formData.password,
      });
      toast.success("✅ Login Successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login Failed: " + err.response?.data?.error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const newErrors = {};
    for (const key in formData) {
      if (key === "studentId") continue;
      const error = validate(key, formData[key]);
      if (error) newErrors[key] = error;
    }
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      toast.error("❌ Fix registration errors");
      return;
    }

    if (!formData.studentId?.type?.startsWith("image/")) {
      toast.error("❌ Upload image file only");
      return;
    }

    const data = new FormData();
    Object.entries(formData).forEach(([k, v]) => data.append(k, v));

    try {
      await api.post("/api/auth/register", data);
      toast.success("✅ Registered Successfully");
      setMode("login");
      setFormData(initialForm);
    } catch (err) {
      toast.error("❌ Register Failed: " + err.response?.data?.error);
    }
  };

  return (
    <div className="relative min-h-screen flex items-center justify-center bg-white dark:bg-gray-900 text-gray-800 dark:text-white transition-colors">
      <BackgroundAnimation />
      <div className="relative z-10 w-full max-w-xl p-8 rounded-2xl bg-white/80 dark:bg-black/50 backdrop-blur shadow-lg">
        <h1 className="text-3xl font-bold text-center mb-6">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button onClick={() => setMode("login")} className={`px-4 py-2 rounded ${mode === "login" ? "bg-blue-600 text-white" : "bg-gray-200 dark:bg-gray-700"}`}>Login</button>
          <button onClick={() => setMode("register")} className={`px-4 py-2 rounded ${mode === "register" ? "bg-green-600 text-white" : "bg-gray-200 dark:bg-gray-700"}`}>Register</button>
        </div>

        {mode === "login" ? (
          <form onSubmit={handleLogin} className="space-y-4">
            <FloatingInput label="Email" name="email" type="email" value={formData.email} error={errors.email} onChange={handleChange} onBlur={handleBlur} />
            <FloatingInput label="Password" name="password" type="password" value={formData.password} error={errors.password} onChange={handleChange} onBlur={handleBlur} />
            <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 transition text-white font-semibold py-2 rounded">Login</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                navigate("/dashboard");
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <form onSubmit={handleRegister} className="space-y-4" encType="multipart/form-data">
            <FloatingInput label="Username" name="username" value={formData.username} error={errors.username} onChange={handleChange} onBlur={handleBlur} />
            <FloatingInput label="Full Name" name="fullname" value={formData.fullname} error={errors.fullname} onChange={handleChange} onBlur={handleBlur} />
            <FloatingInput label="Institute" name="institute" value={formData.institute} error={errors.institute} onChange={handleChange} onBlur={handleBlur} />
            <div className="grid grid-cols-3 gap-2">
              <FloatingInput label="Street" name="street" value={formData.street} onChange={handleChange} />
              <FloatingInput label="City" name="city" value={formData.city} onChange={handleChange} />
              <FloatingInput label="Pincode" name="pincode" value={formData.pincode} onChange={handleChange} />
            </div>
            <FloatingInput label="Age" name="age" type="number" value={formData.age} error={errors.age} onChange={handleChange} onBlur={handleBlur} />
            <select name="course" value={formData.course} onChange={handleChange} onBlur={handleBlur} className="w-full px-3 py-2 bg-white dark:bg-gray-800 border rounded text-sm outline-none">
              <option value="">-- Select Course --</option>
              <option value="BCA">BCA</option>
              <option value="MCA">MCA</option>
              <option value="B.Tech">B.Tech</option>
              <option value="MBA">MBA</option>
            </select>
            {errors.course && <p className="text-sm text-red-500">{errors.course}</p>}

            <FloatingInput label="Email" name="email" type="email" value={formData.email} error={errors.email} onChange={handleChange} onBlur={handleBlur} />
            <div className="flex gap-2">
              <select name="countryCode" className="w-[100px] px-3 py-2 bg-white dark:bg-gray-800 border rounded text-sm" onChange={handleChange} value={formData.countryCode}>
                <option value="+91">+91</option>
                <option value="+1">+1</option>
                <option value="+44">+44</option>
              </select>
              <FloatingInput label="Phone" name="phone" value={formData.phone} error={errors.phone} onChange={handleChange} onBlur={handleBlur} />
            </div>
            <input type="file" name="studentId" accept="image/*" className="w-full text-sm" onChange={handleChange} />
            <FloatingInput label="Password" name="password" type="password" value={formData.password} error={errors.password} onChange={handleChange} onBlur={handleBlur} />
            <FloatingInput label="Confirm Password" name="confirmPassword" type="password" value={formData.confirmPassword} error={errors.confirmPassword} onChange={handleChange} onBlur={handleBlur} />
            <button type="submit" className="w-full bg-green-600 hover:bg-green-700 transition text-white font-semibold py-2 rounded">Register</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                toast.success("✅ Google signup success");
              }}
              onError={() => toast.error("❌ Google signup failed")}
            />
          </form>
        )}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
}
