import React, { useState } from "react";
import api from "../utils/api";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { jwtDecode } from "jwt-decode";
import { GoogleLogin } from "@react-oauth/google";

export default function HomePage() {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    fullname: "",
    username: "",
    email: "",
    phone: "",
    institute: "",
    course: "",
    password: "",
    studentId: null,
  });

  const handleInputChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "studentId") {
      setFormData({ ...formData, studentId: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLogin) {
        const res = await api.post("/api/auth/login", {
          email: formData.email,
          password: formData.password,
        });
        toast.success("✅ Login Successful");
        localStorage.setItem("token", res.data.token);
        localStorage.setItem("user", JSON.stringify(res.data.user));
        navigate("/dashboard");
      } else {
        const form = new FormData();
        for (let key in formData) {
          form.append(key, formData[key]);
        }

        const res = await api.post("/api/auth/register", form);
        toast.success("✅ Registration Successful, Please Login");
        setIsLogin(true);
      }
    } catch (err) {
      console.error("❌ Auth Error:", err);
      toast.error(err?.response?.data?.message || "Something went wrong");
    }
  };

  const handleGoogleLogin = async (credentialResponse) => {
    try {
const decoded = jwtDecode(credentialResponse.credential);
      const res = await api.post("/api/auth/google-login", {
        email: decoded.email,
        name: decoded.name,
      });

      toast.success("✅ Google Login Successful");
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      navigate("/dashboard");
    } catch (err) {
      toast.error("Google Login Failed");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-700 to-indigo-900 flex items-center justify-center p-4">
      <div className="bg-white p-8 rounded-xl shadow-xl w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center">
          {isLogin ? "Login" : "Register"}
        </h1>

        <form onSubmit={handleSubmit} className="space-y-4">
          {!isLogin && (
            <>
              <input
                type="text"
                name="fullname"
                placeholder="Full Name"
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
              <input
                type="text"
                name="username"
                placeholder="Username"
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
              <input
                type="text"
                name="phone"
                placeholder="Phone Number"
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
              <input
                type="text"
                name="institute"
                placeholder="Institute"
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
              <input
                type="text"
                name="course"
                placeholder="Course"
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
              <input
                type="file"
                name="studentId"
                onChange={handleInputChange}
                className="w-full p-2 border rounded"
                required
              />
            </>
          )}

          <input
            type="email"
            name="email"
            placeholder="Email"
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            onChange={handleInputChange}
            className="w-full p-2 border rounded"
            required
          />

          <button
            type="submit"
            className="w-full py-2 bg-purple-700 text-white rounded hover:bg-purple-800"
          >
            {isLogin ? "Login" : "Register"}
          </button>
        </form>

        <div className="mt-4 text-center">
          {isLogin ? (
            <p>
              Don't have an account?{" "}
              <button
                onClick={() => setIsLogin(false)}
                className="text-purple-700 font-semibold"
              >
                Register
              </button>
            </p>
          ) : (
            <p>
              Already have an account?{" "}
              <button
                onClick={() => setIsLogin(true)}
                className="text-purple-700 font-semibold"
              >
                Login
              </button>
            </p>
          )}
        </div>

        <div className="mt-6">
          <GoogleLogin onSuccess={handleGoogleLogin} onError={() => toast.error("Google Login Failed")} />
        </div>
      </div>
    </div>
  );
}
