import React, { useState, useEffect } from "react";
import { GoogleLogin } from "@react-oauth/google";
import { jwtDecode } from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import api from "../utils/api";
import "./HomePage.css";

function BackgroundAnimation() {
  useEffect(() => {
    const canvas = document.getElementById("bg-canvas");
    const ctx = canvas.getContext("2d");
    let width = (canvas.width = window.innerWidth);
    let height = (canvas.height = window.innerHeight);
    let particles = [];

    for (let i = 0; i < 100; i++) {
      particles.push({
        x: Math.random() * width,
        y: Math.random() * height,
        r: Math.random() * 2 + 1,
        dx: Math.random() * 0.5 - 0.25,
        dy: Math.random() * 0.5 - 0.25,
      });
    }

    function draw() {
      ctx.clearRect(0, 0, width, height);
      particles.forEach((p) => {
        ctx.beginPath();
        ctx.arc(p.x, p.y, p.r, 0, Math.PI * 2);
        ctx.fillStyle = "#ffffff88";
        ctx.fill();
        p.x += p.dx;
        p.y += p.dy;

        if (p.x < 0 || p.x > width) p.dx *= -1;
        if (p.y < 0 || p.y > height) p.dy *= -1;
      });
      requestAnimationFrame(draw);
    }

    draw();
  }, []);

  return <canvas id="bg-canvas" className="absolute top-0 left-0 z-0" />;
}

export default function HomePage() {
  const [mode, setMode] = useState("login");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const form = e.target;
    const data = {
      email: form.email.value,
      password: form.password.value,
    };
    try {
      const res = await api.post("/api/auth/login", data);
      toast.success("✅ Login Successful");
      localStorage.setItem("token", res.data.token);
      navigate("/dashboard");
    } catch (err) {
      toast.error("❌ Login Failed: " + err.response?.data?.error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    try {
      await api.post("/api/auth/register", formData);
      toast.success("✅ Registered Successfully");
      setMode("login");
    } catch (err) {
      toast.error("❌ Register Failed: " + err.response?.data?.error);
    }
  };

  return (
    <div
      className="relative h-screen flex items-center justify-center bg-cover bg-center text-white"

    >
      <BackgroundAnimation />
      <div className="relative z-10 bg-black bg-opacity-60 p-10 rounded-2xl w-full max-w-lg text-center shadow-2xl">
        <h1 className="text-3xl font-bold mb-4">Student Event Portal</h1>
        <div className="flex justify-center gap-4 mb-6">
          <button
            onClick={() => setMode("login")}
            className={`px-4 py-2 rounded ${
              mode === "login" ? "bg-blue-500" : "bg-gray-600"
            }`}
          >
            Login
          </button>
          <button
            onClick={() => setMode("register")}
            className={`px-4 py-2 rounded ${
              mode === "register" ? "bg-blue-500" : "bg-gray-600"
            }`}
          >
            Register
          </button>
        </div>

        {mode === "login" ? (
          <form className="space-y-4 animate-fadeIn" onSubmit={handleLogin}>
            <input
              name="email"
              type="email"
              placeholder="Email"
              className="w-full px-4 py-2 rounded bg-gray-700 text-white"
              required
            />
            <input
              name="password"
              type="password"
              placeholder="Password"
              className="w-full px-4 py-2 rounded bg-gray-700 text-white"
              required
            />
            <button className="w-full bg-blue-600 py-2 rounded hover:bg-blue-700">
              Login
            </button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                console.log("Google User:", user);
                toast.success("✅ Google login success");
                localStorage.setItem("user", JSON.stringify(user));
                navigate("/dashboard");
              }}
              onError={() => toast.error("❌ Google login failed")}
            />
          </form>
        ) : (
          <form className="space-y-3 animate-fadeIn" onSubmit={handleRegister}>
            <input name="username" type="text" placeholder="Username" required className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="fullname" type="text" placeholder="Full Name" className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="institute" type="text" placeholder="College/School" className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="address" type="text" placeholder="Address" className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="age" type="number" placeholder="Age" className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="course" type="text" placeholder="Course" className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="email" type="email" placeholder="Email" required className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="phone" type="tel" placeholder="Phone" className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="studentId" type="file" className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <input name="password" type="password" placeholder="Password" required className="w-full px-4 py-2 rounded bg-gray-700 text-white" />
            <button className="w-full bg-green-600 py-2 rounded hover:bg-green-700">Register</button>
            <GoogleLogin
              onSuccess={(cred) => {
                const user = jwtDecode(cred.credential);
                console.log("Google Signup User:", user);
                toast.success("✅ Google signup success");
              }}
              onError={() => toast.error("❌ Google signup failed")}
            />
          </form>
        )}
        <ToastContainer position="top-right" autoClose={3000} />
      </div>
    </div>
  );
}
